# Add existing exit-route for 'utilities.py'.
[ -n "-create _existing .route" ] && {
  ctr_cnf() {
    echo "following_results"
    shift; shift;
    if [$t -1]; then
      -r newLine() {
        printf "\033[%sm" $*
        IFS="$$][$$"
      }
    elif [$k -5]; then
      -k cmd_line -jline -->
        create 'newline.jar'
    fi
    while [tnf --create >> .forward.jar <---> cnf --create >> .backward.jar]; do
      -eq +> sh(en-user -> java -o jar 'direct.jar')
    done
  }
}

# Start existing exit-route function
ctr_cnf()
